mapper.update:
UPDATE PROJECT SET 
	PRO_SUBJECT=#{proSubject},
	PRO_EXP=#{proExp},
	PUBLIC_YN=#{publicYN}
WHERE PRO_NO=#{proNo}

UPDATE PRO_Board SET 
	BOARD_TEXT=#{boardText}
WHERE BOARD_NO=#{boardNo}

UPDATE PRO_BOARD SET 
	DEL_YN='Y'
WHERE BOARD_NO=#{boardNo}

UPDATE PRO_MEMBER SET 
	DEL_YN='Y'
WHERE PRO_NO=#{proNo} AND MEM_NO=#{memNo}

UPDATE PROJECT SET 
	DEL_YN='Y'
WHERE PRO_NO=#{proNo}

UPDATE PRO_MEMBER SET 
	MGR_YN='Y'
WHERE PRO_NO=#{proNo} AND MEM_NO=#{memNo}

UPDATE PRO_MEMBER SET 
	MGR_YN='N'
WHERE PRO_NO=#{proNo} AND MEM_NO=#{memNo}

UPDATE PROJECT SET 
	COMP_YN=#{compYN}
WHERE PRO_NO=#{proNo}
